// KH·ªûI T·∫†O BI·∫æN 
var mode = flow.get("mode") || "manual";
// manualState: L∆∞u tr·∫°ng th√°i n√∫t b·∫•m (ON/OFF)
var manualState = flow.get("manualState") || "OFF";
// isPumping: L∆∞u tr·∫°ng th√°i th·ª±c t·∫ø b∆°m ƒëang ch·∫°y hay kh√¥ng
var isPumping = flow.get("isPumping") || false;
var mqttTopic = "irrigation/control";

// L·∫•y c√†i ƒë·∫∑t Auto
var soilLimit = Number(flow.get("soilLimit")) || 30;
var humLimit = Number(flow.get("humLimit")) || 40;
var tempLimit = Number(flow.get("tempLimit")) || 35;

// H√ÄM LOGIC AUTO 
function getAutoDecision(s) {
    if (!s) return "OFF";
    var curSoil = Number(s.soil);
    var curHum = Number(s.hum);
    var curTemp = Number(s.temp);
    var needWater = (curSoil < soilLimit);
    if (curHum < humLimit && curSoil < soilLimit) needWater = true;
    if (curTemp > tempLimit) return "OFF";
    if (needWater) return "ON";
    if (!needWater) return "OFF";
    return "KEEP";
}

// X·ª¨ L√ù L·ªÜNH T·ª™ N√öT B·∫§M (BUTTON CLICK)
if (msg.cmd === "PUMP_ON" || msg.cmd === "PUMP_OFF") {
    var cmd = (msg.cmd === "PUMP_ON") ? "ON" : "OFF";
    // L∆ØU TR·∫†NG TH√ÅI V√ÄO B·ªò NH·ªö
    flow.set("manualState", cmd);
    // Debug: In ra ƒë·ªÉ xem ƒë√£ l∆∞u ch∆∞a
    node.warn(`[DEBUG CLICK] ƒê√£ l∆∞u manualState: ${cmd}`);
    // Chu·∫©n b·ªã l∆∞u manualState l√™n cloud
    var saveManualStateMsg = {
        topic: "save_manual_state",
        payload: cmd
    };

    if (mode === "manual") {
        flow.set("isPumping", (cmd === "ON"));
        // Output 1: MQTT, Output 2: null, Output 3: Save manualState
        return [{ topic: mqttTopic, payload: cmd }, null, saveManualStateMsg];
    }
    return [null, null, saveManualStateMsg];
}

// X·ª¨ L√ù CHUY·ªÇN CH·∫æ ƒê·ªò
if (msg.topic === "cmd_mode") {
    mode = msg.payload;
    flow.set("mode", mode);
    var cmdToSend = "OFF";

    if (mode === "manual") {
        // KH√îI PH·ª§C TR·∫†NG TH√ÅI C≈®
        cmdToSend = flow.get("manualState") || "OFF";
        node.warn(`[DEBUG MODE] Chuy·ªÉn Manual -> Kh√¥i ph·ª•c l·∫°i: ${cmdToSend}`);
    } else {
        // T√çNH TO√ÅN AUTO
        var s = flow.get("lastSensors");
        if (s) {
            var decision = getAutoDecision(s);
            cmdToSend = (decision === "KEEP") ? (isPumping ? "ON" : "OFF") : decision;
        } else {
            cmdToSend = "OFF";
        }
    }

    flow.set("isPumping", (cmdToSend === "ON"));

    // G·ª≠i c·∫≠p nh·∫≠t v·ªÅ UI ƒë·ªÉ UI ƒë·ªïi ch·ªØ Watering/Stopped cho ƒë√∫ng
    var uiUpdate = {
        mode: mode,
        pump: cmdToSend.toLowerCase()
    };

    // Chu·∫©n b·ªã l∆∞u mode v√†o MongoDB
    var modeMsg = {
        topic: "save_mode",
        payload: mode
    };

    node.warn(`[CHANGE MODE] User chuy·ªÉn sang: ${mode}`);

    return [{ topic: mqttTopic, payload: cmdToSend }, { topic: "current_settings", payload: uiUpdate }, modeMsg];
}

// X·ª¨ L√ù SETTINGS
if (msg.topic === "cmd_settings") {
    // C·∫≠p nh·∫≠t settings v√†o flow context
    if (msg.payload.soilLimit !== undefined) flow.set("soilLimit", Number(msg.payload.soilLimit));
    if (msg.payload.humLimit !== undefined) flow.set("humLimit", Number(msg.payload.humLimit));
    if (msg.payload.tempLimit !== undefined) flow.set("tempLimit", Number(msg.payload.tempLimit));

    // Tr·∫£ v·ªÅ settings m·ªõi cho web
    var updatedSettings = {
        settings: {
            soilLimit: flow.get("soilLimit") || 30,
            humLimit: flow.get("humLimit") || 40,
            tempLimit: flow.get("tempLimit") || 35
        },
        mode: mode,
        pump: isPumping ? "on" : "off"
    };

    node.warn(`[UPDATE SETTINGS] Soil: ${updatedSettings.settings.soilLimit}, Hum: ${updatedSettings.settings.humLimit}, Temp: ${updatedSettings.settings.tempLimit}`);

    // Output 1: G·ª≠i msg ƒë·ªÉ l∆∞u MongoDB, Output 2: G·ª≠i settings v·ªÅ web
    return [msg, { topic: "current_settings", payload: updatedSettings }];
}

// X·ª¨ L√ù LOAD L·∫†I TRANG (GET_SETTINGS)
if (msg.topic === "get_settings") {
    var displayPumpState = "off";
