    var s = msg.payload.data;
    flow.set("lastSensors", s);

    if (mode === "manual") return null;

    var decision = getAutoDecision(s);
    if (decision === "ON" && !isPumping) {
        flow.set("isPumping", true);
        return [{ topic: mqttTopic, payload: "ON" }, null];
    }
    if (decision === "OFF" && isPumping) {
        flow.set("isPumping", false);
        return [{ topic: mqttTopic, payload: "OFF" }, null];
    }
}

